(() => {
  // <stdin>
  (function($) {
    "use strict";
    const nav = document.querySelector(".navbar");
    const first_section = $(".c-band");
    const navHeight = nav.offsetHeight;
    function srcTo(el) {
      $("html, body").animate({
        scrollTop: el.offset().top - navHeight
      }, 1e3);
    }
    $("#header-arrow").click(function() {
      srcTo(first_section);
    });
    $(".c-category:odd").addClass("js-odd");
  })(jQuery);
})();
